function backtrace(node)
{
    var path = [[node.x, node.y]];
	
    while (node.parent) {
        node = node.parent;
        path.push([node.x, node.y]);
    }
	
    return path;
}

/**
 * Find and return the the path.
 * @return {Array.<[number, number]>} The path, including both start and
 *     end positions.
 */
 

module.exports.findPath = function(startX, startY, endX, endY, map) {

	map.resetPath();
	
	var openList = new Heap(function(nodeA, nodeB) {
		return nodeA.f - nodeB.f;
	}),
	
	startNode = map.getNodeAt(startX, startY),
	endNode = map.getNodeAt(endX, endY),
	heuristic = function(dx, dy){return dx + dy;}, //this.heuristic,
	allowDiagonal = true,
	dontCrossCorners = true,
	weight = 1,
	abs = Math.abs, SQRT2 = Math.SQRT2,
	node, neighbors, neighbor, i, l, x, y, ng;

	if (startNode.d != 0 || endNode.d != 0) {
		return [];
	}
	
    // set the `g` and `f` value of the start node to be 0
    startNode.g = 0;
    startNode.f = 0;


    // push the start node into the open list
    openList.push(startNode);
    startNode.opened = true;

    // while the open list is not empty
    while (!openList.empty()) {
        // pop the position of node which has the minimum `f` value.
        node = openList.pop();
        node.closed = true;

        // if reached the end position, construct the path and return it
        if (node === endNode) {
            return backtrace(endNode);
        }

        // get neigbours of the current node
        neighbors = map.getNeighbors(node);

        for (i = 0, l = neighbors.length; i < l; ++i) {
            neighbor = neighbors[i];

            if (neighbor.closed) {
                continue;
            }

            x = neighbor.x;
            y = neighbor.y;

            // get the distance between current node and the neighbor
            // and calculate the next g score
            ng = node.g + ((x - node.x === 0 || y - node.y === 0) ? 1 : SQRT2);

            // check if the neighbor has not been inspected yet, or
            // can be reached with smaller cost from the current node
            if (!neighbor.opened || ng < neighbor.g) {
                neighbor.g = ng;
                neighbor.h = neighbor.h || weight * heuristic(abs(x - node.x), abs(y - node.y));
                neighbor.f = neighbor.g + neighbor.h;
                neighbor.parent = node;

                if (!neighbor.opened) {
                    openList.push(neighbor);
                    neighbor.opened = true;
                } else {
                    // the neighbor can be reached with smaller cost.
                    // Since its f value has been updated, we have to
                    // update its position in the open list
                    openList.updateItem(neighbor);
                }
            }
        } // end for each neighbor
    } // end while not open list empty

    // fail to find the path
    return [];
};