var DB = require('db');
var ServerState = new require('serverState');

// Constructor
function Client() {
    if(false === (this instanceof Client)) {
		return new Client();
    }
}

Client.prototype.onConnected = function(conn) {
	this.connection = conn;
	this.internalId = ++clientIndexCounter;
}

Client.prototype.onDisconnected = function()
{			
	this.onLeave();
	
    this.connection = null;
    this.gameSession = null;
}

Client.prototype.onLeave = function()
{
	if (this.gameSession)
    {
		this.gameSession.removeClient(this);
		this.gameSession.checkActive();
		this.gameSession.sendMessageToAllClients(null, {'method': 'clientLeft', 'result': {'gameActive': this.gameSession.active, 'clientIndex': this.index, 'client': {'name': this.name}}});
		this.gameSession = null;
    }
}

Client.prototype.sendMessage = function(message) {
	this.connection.send(JSON.stringify(message));
}

// Memeber variables
Client.prototype.connection = null;
Client.prototype.id = 0;
Client.prototype.sessionId = null;
Client.prototype.internalId = 0;
Client.prototype.index = -1;
Client.prototype.gameSession = null;
Client.prototype.name = null;

// Export for NodeJS
module.exports = Client;

var clientIndexCounter = 0;