var $ = require('jquery');
var GameSession = require('gameSession');
var DB = require('db');
var Utils = require('utils');

var gslen = 0;
var clients = [];
var gameSessions = [];

module.exports.addClient = function(client) {
	clients.push(client);
	// console.log("# Client count (join): " + clients.length);
}

module.exports.removeClient = function(client) {
	clients = $.grep(
		clients,
		function(o,i) { return o.internalId === client.internalId; },
        true);
	// console.log("# Client count (leave): " + clients.length);
}

module.exports.createGameSession = function(client, params)
{
    if (client.gameSession == null)
	{
        var session = new GameSession(params);
		gameSessions.push(session);
		
        if (session.joinClient(client) == false)
		{
			console.log('Session create join failed');
		}
		else
		{
			// Let all clients currently not in a game know that the lobby list changed
		
			$.each(clients, function(ci, c) {
				if (c.gameSession == null) {
					c.sendMessage({"method": "lobbyUpdated", "result": {}});
				}
			});
		}
		
        this.sendJoinGameSessionResponse(client, session, 'createGameSession');
    }
	else
	{
        client.sendMessage({"method": "createGameSession", "result": {"success": false}});
    }
}

module.exports.removeGameSession = function(id)
{
    for (k in gameSessions)
	{
        if (gameSessions[k].id == id)
		{
            gameSessions[k].clean();
			gameSessions[k] = null;
            gameSessions.splice(k, 1);
			
			// Let all clients currently not in a game know that the lobby list changed
		
			$.each(clients, function(ci, c) {
				if (c.gameSession == null) {
					c.sendMessage({"method": "lobbyUpdated", "result": {}});
				}
			});
			
            break;
        }
    }
}

module.exports.endGame = function(winnerClient, loserClients, gameSessionId)
{
	console.log('GAME: ' + gameSessionId);

	if (winnerClient)
	{
		console.log('WINNER: ' + winnerClient.name);
		
		DB.increaseWins(winnerClient.id, function(error, result) { });
		
		winnerClient.sendMessage({
			"method": "gameOver",
			"result": {
				"winner": true
			}
		});
	}
	
	for (var i = 0; i < loserClients.length; ++i)
	{
		if (loserClients[i] == null) {
			continue;
		}
		
		console.log("loser name = " + loserClients[i].name);
		
		DB.increaseLosses(loserClients[i].id, function(error, result) { });
		
		loserClients[i].sendMessage({
			"method": "gameOver",
			"result": {
				"winner": false
			}
		});
	}
	
	this.removeGameSession(gameSessionId);
}

module.exports.getGameSessions = function()
{
    var sessions = [];
    
    for (x in gameSessions)
    {
        var players = 0;
        
        for (c in gameSessions[x].players)
            if (gameSessions[x].players[c] != null && gameSessions[x].players[c].client != null)
                players++;
        
        sessions.push({
            "id": gameSessions[x].id,
            "name": gameSessions[x].name,
            "players": players,
            "maxPlayers": gameSessions[x].maxPlayers,
			"private": gameSessions[x].password.length > 0 ? true : false,
			"active": gameSessions[x].active,
			"map": {
				"width": gameSessions[x].map.width,
				"height": gameSessions[x].map.height
			},
        });
    }
    
    return sessions;
}

module.exports.getGameSessionById = function(id)
{
    for (x in gameSessions) {
        if (gameSessions[x].id == id) return gameSessions[x];
    }
    
    return null;
}

module.exports.joinGameSession = function(client, params)
{
    if (client.gameSession != null)
	{
		console.log("Client " + client.id + " already has a game session..");
        return;
    }

    for(x in gameSessions)
	{
        if (gameSessions[x].id == params.gameSession)
		{
            if (gameSessions[x].password.length > 0 && gameSessions[x].password != params.password)
			{
				client.sendMessage({
					"method": "join",
					"result": {
						"success": false,
						"reason": "wrongpass"
					}
				});
				
				return;
			}
			
			if (gameSessions[x].joinClient(client) == true)
			{
				gameSessions[x].checkActive();
			
				this.sendJoinGameSessionResponse(client, gameSessions[x], 'join');
			
				// console.log("Client " + client.id + " joined game session " + params.gameSession);

				gameSessions[x].sendMessageToAllClientsExcept(null, {
					'method': 'clientJoined',
					'result': {
						'gameActive': gameSessions[x].active,
						'clientIndex': client.index,
						'client': {
							'name': client.name
						},
						'buildings': gameSessions[x].players[client.index].buildings
					}
				}, client.id);
			}
			else
			{
				client.sendMessage({
					"method": "join",
					"result": {
						"success": false,
						"reason": "roomfull"
					}
				});
			}
			
            break;
        }
    }
}



module.exports.tick = function()
{
	/*
		Update all game sessions
	*/
	
	gslen = gameSessions.length;
	
	while (gslen--) {
		gameSessions[gslen].tick();
	}
}

module.exports.slowTick = function()
{
	/*
		Update all game sessions
	*/
	
	gslen = gameSessions.length;
	
	while (gslen--) {
		gameSessions[gslen].slowTick();
	}
}


module.exports.sendMessageToAllClientsExcept = function(message, client_id) {
    for (c in clients) {
        if (clients[c] != null)
			if (clients[c].id != client_id)
				clients[c].sendMessage(message);
    }
}

module.exports.sendMessageToAllClients = function(message) {
    for (c in clients) {
        if (clients[c] != null)
            clients[c].sendMessage(message);
    }
}


module.exports.sendJoinGameSessionResponse = function(client, session, methodName)
{
	client.sendMessage({
        "method": methodName,
        "result": {
            "success": true,
            "gameSession": session.id,
			"sessionName": session.name,
            "clientIndex": client.index,
            "maxPlayers": session.maxPlayers,
			"resources": session.players[client.index].resources,
            "map": {"data": session.map.data, "width": session.map.width, "height": session.map.height},
            "buildings": session.getBuildingLists(),
            "units": session.getUnitLists(),
            "baseBuildings": GameSession.baseBuildings,
            "baseUnits": GameSession.baseUnits,
			"clients": session.getClientsInfos(),
			"upgrade": session.players[client.index].upgrade,
			"power": session.players[client.index].power,
			"active": session.active
        }
    });
}


/*
	Generate a session id and insert it for client's id
*/
function createSession(client)
{
	client.sessionId = Utils.generateID(16);
	
	DB.insertSession(client.id, client.sessionId, function(error, result2)
	{
		client.sendMessage({'method': 'login', 'result': {'authenticated': true, 'session': client.sessionId}});
	});
}

module.exports.performLogout = function(client)
{
	client.onLeave();
	
	DB.deleteSession(client.sessionId, function(error, result)
	{
		client.sessionId = null;
		client.id = 0;
	});
}

module.exports.performLogin = function(client, params)
{
	// console.log('performLogin');
	// console.log(params);
	
	/*
		Logging in using a session id
	*/
	if (params.session)
	{
		/*
			Get user id matching this session id
		*/
		DB.getSession(params.session, function(error, sessionResult)
		{
			if (sessionResult.length == 0) {
				client.sendMessage({'method': 'login', 'result': {'authenticated': false, 'error': 'no_session'}});
				return;
			}

			DB.getUserById(sessionResult[0].userId, function(error, userInfoResult)
			{
				if (userInfoResult.length == 0) {
					client.sendMessage({'method': 'login', 'result': {'authenticated': false, 'error': 'no_session'}});
					return;
				}
				
				client.id = sessionResult[0].userId;
				client.sessionId = params.session;
				client.name = userInfoResult[0].username;
				
				client.sendMessage({'method': 'login', 'result': {'authenticated': true}});

				// ServerState.sendMessageToAllClientsExcept({'method': 'msg', 'result': {'lobby': true, 'message': client.name + ' has joined the game!', 'color': 15}}, client.id);
			});
		});
	}
	/*
		Logging in using user/pass
	*/
	else
	{
		DB.getUserByUsername(params.user, function(error, userResult)
		{
			/*
				User does not exist
			*/
			
			if (userResult.length == 0)
			{
				/*
					Insert new user
				*/
				DB.insertUser(params.user, params.pass, function(error, result) {

					client.id = result.insertId;
					client.name = params.user;
					
					createSession(client);
				});
			}
			/*
				User exists
			*/
			else
			{
				/*
					Password is correct
				*/
				if (userResult[0].password == Utils.SHA1(params.pass))
				{
					
					client.id = userResult[0].id;
					client.name = params.user;
					
					DB.deleteUserSessions(client.id, function(error, sessionResult) {
						createSession(client);
					});
				}
				/*
					Incorrect password
				*/
				else
				{
					client.sendMessage({'method': 'login', 'result': {'authenticated': false, 'error': 'wrong_pass'}});
				}
			}
		});
	}
}